package pt.ua.tqs.fourwheels.controllers;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.*;
import pt.ua.tqs.fourwheels.components.Profile;
import pt.ua.tqs.fourwheels.components.ProfileRepository;

import java.util.Optional;

@Controller
@RequestMapping("/profile")
public class ProfileController {
    @Autowired // This means to get the bean called userRepository
    // Which is auto-generated by Spring, we will use it to handle the data
    private ProfileRepository userRepository;

    @GetMapping("/profile")
    public @ResponseBody Optional<Profile> getInfo(@RequestParam(value = "ID", defaultValue = "1") String id){
        return userRepository.findById(Integer.parseInt(id));
    }
}
